#!/bin/bash
# Uninstall script for FSNotes
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling FSNotes..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping FSNotes if running..."
pkill -f "FSNotes" 2>/dev/null || true

# Remove /Applications/FSNotes.app
echo "Removing /Applications/FSNotes.app..."
if [ -d "/Applications/FSNotes.app" ]; then
    rm -rf "/Applications/FSNotes.app" 2>/dev/null || true
elif [ -f "/Applications/FSNotes.app" ]; then
    rm -f "/Applications/FSNotes.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/co.fluder.FSNotes
echo "Removing $HOME/Library/Application Scripts/co.fluder.FSNotes..."
if [ -d "$HOME/Library/Application Scripts/co.fluder.FSNotes" ]; then
    rm -rf "$HOME/Library/Application Scripts/co.fluder.FSNotes" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/co.fluder.FSNotes" ]; then
    rm -f "$HOME/Library/Application Scripts/co.fluder.FSNotes" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/co.fluder.FSNotes
echo "Removing $HOME/Library/Containers/co.fluder.FSNotes..."
if [ -d "$HOME/Library/Containers/co.fluder.FSNotes" ]; then
    rm -rf "$HOME/Library/Containers/co.fluder.FSNotes" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/co.fluder.FSNotes" ]; then
    rm -f "$HOME/Library/Containers/co.fluder.FSNotes" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
