#!/bin/bash
# Uninstall script for Garmin Express
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Garmin Express..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Garmin Express if running..."
pkill -f "Garmin Express" 2>/dev/null || true

# Kill application with bundle ID com.garmin.renu.client if running
echo "Stopping application with bundle ID com.garmin.renu.client if running..."
killall -9 "com.garmin.renu.client" 2>/dev/null || true

# Kill application with bundle ID com.garmin.renu.service if running
echo "Stopping application with bundle ID com.garmin.renu.service if running..."
killall -9 "com.garmin.renu.service" 2>/dev/null || true

# Remove $HOME/Library/Application Support/Garmin/Express
echo "Removing $HOME/Library/Application Support/Garmin/Express..."
if [ -d "$HOME/Library/Application Support/Garmin/Express" ]; then
    rm -rf "$HOME/Library/Application Support/Garmin/Express" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Garmin/Express" ]; then
    rm -f "$HOME/Library/Application Support/Garmin/Express" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.garmin.renu.client
echo "Removing $HOME/Library/Caches/com.garmin.renu.client..."
if [ -d "$HOME/Library/Caches/com.garmin.renu.client" ]; then
    rm -rf "$HOME/Library/Caches/com.garmin.renu.client" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.garmin.renu.client" ]; then
    rm -f "$HOME/Library/Caches/com.garmin.renu.client" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.garmin.renu.service
echo "Removing $HOME/Library/Caches/com.garmin.renu.service..."
if [ -d "$HOME/Library/Caches/com.garmin.renu.service" ]; then
    rm -rf "$HOME/Library/Caches/com.garmin.renu.service" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.garmin.renu.service" ]; then
    rm -f "$HOME/Library/Caches/com.garmin.renu.service" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.garmin.renu.service.crashreporter
echo "Removing $HOME/Library/Caches/com.garmin.renu.service.crashreporter..."
if [ -d "$HOME/Library/Caches/com.garmin.renu.service.crashreporter" ]; then
    rm -rf "$HOME/Library/Caches/com.garmin.renu.service.crashreporter" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.garmin.renu.service.crashreporter" ]; then
    rm -f "$HOME/Library/Caches/com.garmin.renu.service.crashreporter" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.garmin.renu*
echo "Removing $HOME/Library/Preferences/com.garmin.renu*..."
if [ -d "$HOME/Library/Preferences/com.garmin.renu*" ]; then
    rm -rf "$HOME/Library/Preferences/com.garmin.renu*" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.garmin.renu*" ]; then
    rm -f "$HOME/Library/Preferences/com.garmin.renu*" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
