#!/bin/bash
# Uninstall script for GDevelop
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling GDevelop..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping GDevelop if running..."
pkill -f "GDevelop" 2>/dev/null || true

# Remove /Applications/GDevelop 5.app
echo "Removing /Applications/GDevelop 5.app..."
if [ -d "/Applications/GDevelop 5.app" ]; then
    rm -rf "/Applications/GDevelop 5.app" 2>/dev/null || true
elif [ -f "/Applications/GDevelop 5.app" ]; then
    rm -f "/Applications/GDevelop 5.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/GDevelop 5
echo "Removing $HOME/Library/Application Support/GDevelop 5..."
if [ -d "$HOME/Library/Application Support/GDevelop 5" ]; then
    rm -rf "$HOME/Library/Application Support/GDevelop 5" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/GDevelop 5" ]; then
    rm -f "$HOME/Library/Application Support/GDevelop 5" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/GDevelop 5
echo "Removing $HOME/Library/Logs/GDevelop 5..."
if [ -d "$HOME/Library/Logs/GDevelop 5" ]; then
    rm -rf "$HOME/Library/Logs/GDevelop 5" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/GDevelop 5" ]; then
    rm -f "$HOME/Library/Logs/GDevelop 5" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.gdevelop-app.ide.plist
echo "Removing $HOME/Library/Preferences/com.gdevelop-app.ide.plist..."
if [ -d "$HOME/Library/Preferences/com.gdevelop-app.ide.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.gdevelop-app.ide.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.gdevelop-app.ide.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.gdevelop-app.ide.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.gdevelop-app.ide.savedState
echo "Removing $HOME/Library/Saved Application State/com.gdevelop-app.ide.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.gdevelop-app.ide.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.gdevelop-app.ide.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.gdevelop-app.ide.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.gdevelop-app.ide.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
