#!/bin/bash
# Uninstall script for Go2Shell
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Go2Shell..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Go2Shell if running..."
pkill -f "Go2Shell" 2>/dev/null || true

# Remove /Applications/Go2Shell.app
echo "Removing /Applications/Go2Shell.app..."
if [ -d "/Applications/Go2Shell.app" ]; then
    rm -rf "/Applications/Go2Shell.app" 2>/dev/null || true
elif [ -f "/Applications/Go2Shell.app" ]; then
    rm -f "/Applications/Go2Shell.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.zipzapmac.Go2Shell.plist
echo "Removing $HOME/Library/Preferences/com.zipzapmac.Go2Shell.plist..."
if [ -d "$HOME/Library/Preferences/com.zipzapmac.Go2Shell.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.zipzapmac.Go2Shell.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.zipzapmac.Go2Shell.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.zipzapmac.Go2Shell.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
