    rm -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.google.googleearthupdatehelper.sfl*" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Google Earth
echo "Removing $HOME/Library/Application Support/Google Earth..."
if [ -d "$HOME/Library/Application Support/Google Earth" ]; then
    rm -rf "$HOME/Library/Application Support/Google Earth" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Google Earth" ]; then
    rm -f "$HOME/Library/Application Support/Google Earth" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.Google.GoogleEarthPro
echo "Removing $HOME/Library/Caches/com.Google.GoogleEarthPro..."
if [ -d "$HOME/Library/Caches/com.Google.GoogleEarthPro" ]; then
    rm -rf "$HOME/Library/Caches/com.Google.GoogleEarthPro" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.Google.GoogleEarthPro" ]; then
    rm -f "$HOME/Library/Caches/com.Google.GoogleEarthPro" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/Google Earth
echo "Removing $HOME/Library/Caches/Google Earth..."
if [ -d "$HOME/Library/Caches/Google Earth" ]; then
    rm -rf "$HOME/Library/Caches/Google Earth" 2>/dev/null || true
