#!/bin/bash
# Uninstall script for Google Web Designer
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Google Web Designer..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Google Web Designer if running..."
pkill -f "Google Web Designer" 2>/dev/null || true

# Remove /Applications/Google Web Designer.app
echo "Removing /Applications/Google Web Designer.app..."
if [ -d "/Applications/Google Web Designer.app" ]; then
    rm -rf "/Applications/Google Web Designer.app" 2>/dev/null || true
elif [ -f "/Applications/Google Web Designer.app" ]; then
    rm -f "/Applications/Google Web Designer.app" 2>/dev/null || true
fi

# Remove $HOME/Documents/Google Web Designer
echo "Removing $HOME/Documents/Google Web Designer..."
if [ -d "$HOME/Documents/Google Web Designer" ]; then
    rm -rf "$HOME/Documents/Google Web Designer" 2>/dev/null || true
elif [ -f "$HOME/Documents/Google Web Designer" ]; then
    rm -f "$HOME/Documents/Google Web Designer" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Google/Web Designer
echo "Removing $HOME/Library/Application Support/Google/Web Designer..."
if [ -d "$HOME/Library/Application Support/Google/Web Designer" ]; then
    rm -rf "$HOME/Library/Application Support/Google/Web Designer" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Google/Web Designer" ]; then
