fi

# Remove $HOME/Library/Preferences/com.google.WebDesigner.plist
echo "Removing $HOME/Library/Preferences/com.google.WebDesigner.plist..."
if [ -d "$HOME/Library/Preferences/com.google.WebDesigner.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.google.WebDesigner.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.google.WebDesigner.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.google.WebDesigner.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.google.WebDesigner.savedState
echo "Removing $HOME/Library/Saved Application State/com.google.WebDesigner.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.google.WebDesigner.savedState" ]; then
