elif [ -f "$HOME/Library/Saved Application State/com.google.WebDesigner.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.google.WebDesigner.savedState" 2>/dev/null || true
fi

# Remove $HOME/Library/Google
echo "Removing $HOME/Library/Google..."
if [ -d "$HOME/Library/Google" ]; then
    rm -rf "$HOME/Library/Google" 2>/dev/null || true
