#!/bin/bash
# Uninstall script for Granola
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Granola..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Granola if running..."
pkill -f "Granola" 2>/dev/null || true

# Remove /Applications/Granola.app
echo "Removing /Applications/Granola.app..."
if [ -d "/Applications/Granola.app" ]; then
    rm -rf "/Applications/Granola.app" 2>/dev/null || true
elif [ -f "/Applications/Granola.app" ]; then
    rm -f "/Applications/Granola.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Granola
echo "Removing $HOME/Library/Application Support/Granola..."
if [ -d "$HOME/Library/Application Support/Granola" ]; then
    rm -rf "$HOME/Library/Application Support/Granola" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Granola" ]; then
    rm -f "$HOME/Library/Application Support/Granola" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.granola.app
echo "Removing $HOME/Library/Caches/com.granola.app..."
if [ -d "$HOME/Library/Caches/com.granola.app" ]; then
    rm -rf "$HOME/Library/Caches/com.granola.app" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.granola.app" ]; then
