#!/bin/bash
# Uninstall script for Guitar Pro
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Guitar Pro..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Guitar Pro if running..."
pkill -f "Guitar Pro" 2>/dev/null || true

# Remove /Library/Application Support/Arobas Music
echo "Removing /Library/Application Support/Arobas Music..."
if [ -d "/Library/Application Support/Arobas Music" ]; then
    rm -rf "/Library/Application Support/Arobas Music" 2>/dev/null || true
elif [ -f "/Library/Application Support/Arobas Music" ]; then
    rm -f "/Library/Application Support/Arobas Music" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Arobas Music
echo "Removing $HOME/Library/Application Support/Arobas Music..."
if [ -d "$HOME/Library/Application Support/Arobas Music" ]; then
    rm -rf "$HOME/Library/Application Support/Arobas Music" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Arobas Music" ]; then
    rm -f "$HOME/Library/Application Support/Arobas Music" 2>/dev/null || true
fi

# Remove $HOME/Library/HTTPStorages/com.arobas-music.guitarpro*
echo "Removing $HOME/Library/HTTPStorages/com.arobas-music.guitarpro*..."
if [ -d "$HOME/Library/HTTPStorages/com.arobas-music.guitarpro*" ]; then
