elif [ -f "$HOME/Library/Preferences/com.arobas-music.guitarpro*.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.arobas-music.guitarpro*.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.arobas-music.guitarpro*.savedState
echo "Removing $HOME/Library/Saved Application State/com.arobas-music.guitarpro*.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.arobas-music.guitarpro*.savedState" ]; then
