#!/bin/bash
# Uninstall script for Helium
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Helium..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Helium if running..."
pkill -f "Helium" 2>/dev/null || true

# Kill application with bundle ID com.koushikdutta.Helium if running
echo "Stopping application with bundle ID com.koushikdutta.Helium if running..."
killall -9 "com.koushikdutta.Helium" 2>/dev/null || true

# Remove /Applications/Helium.app
echo "Removing /Applications/Helium.app..."
if [ -d "/Applications/Helium.app" ]; then
    rm -rf "/Applications/Helium.app" 2>/dev/null || true
elif [ -f "/Applications/Helium.app" ]; then
    rm -f "/Applications/Helium.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.koushikdutta.Helium.savedState
echo "Removing $HOME/Library/Saved Application State/com.koushikdutta.Helium.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.koushikdutta.Helium.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.koushikdutta.Helium.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.koushikdutta.Helium.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.koushikdutta.Helium.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
