#!/bin/bash
# Uninstall script for Hot
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Hot..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Hot if running..."
pkill -f "Hot" 2>/dev/null || true

# Remove /Applications/Hot.app
echo "Removing /Applications/Hot.app..."
if [ -d "/Applications/Hot.app" ]; then
    rm -rf "/Applications/Hot.app" 2>/dev/null || true
elif [ -f "/Applications/Hot.app" ]; then
    rm -f "/Applications/Hot.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.xs-labs.Hot
echo "Removing $HOME/Library/Caches/com.xs-labs.Hot..."
if [ -d "$HOME/Library/Caches/com.xs-labs.Hot" ]; then
    rm -rf "$HOME/Library/Caches/com.xs-labs.Hot" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.xs-labs.Hot" ]; then
    rm -f "$HOME/Library/Caches/com.xs-labs.Hot" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.xs-labs.Hot.plist
echo "Removing $HOME/Library/Preferences/com.xs-labs.Hot.plist..."
if [ -d "$HOME/Library/Preferences/com.xs-labs.Hot.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.xs-labs.Hot.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.xs-labs.Hot.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.xs-labs.Hot.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
