echo "Removing /Applications/HTTP Toolkit.app..."
if [ -d "/Applications/HTTP Toolkit.app" ]; then
    rm -rf "/Applications/HTTP Toolkit.app" 2>/dev/null || true
elif [ -f "/Applications/HTTP Toolkit.app" ]; then
    rm -f "/Applications/HTTP Toolkit.app" 2>/dev/null || true
fi

