#!/bin/bash
# Uninstall script for ImageJ
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling ImageJ..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping ImageJ if running..."
pkill -f "ImageJ" 2>/dev/null || true

# Remove /Applications/ImageJ.app
echo "Removing /Applications/ImageJ.app..."
if [ -d "/Applications/ImageJ.app" ]; then
    rm -rf "/Applications/ImageJ.app" 2>/dev/null || true
elif [ -f "/Applications/ImageJ.app" ]; then
    rm -f "/Applications/ImageJ.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/ImageJ.savedState
echo "Removing $HOME/Library/Saved Application State/ImageJ.savedState..."
if [ -d "$HOME/Library/Saved Application State/ImageJ.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/ImageJ.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/ImageJ.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/ImageJ.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
