#!/bin/bash
# Uninstall script for iMazing Converter
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling iMazing Converter..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping iMazing Converter if running..."
pkill -f "iMazing Converter" 2>/dev/null || true

# Remove /Applications/iMazing Converter.app
echo "Removing /Applications/iMazing Converter.app..."
if [ -d "/Applications/iMazing Converter.app" ]; then
    rm -rf "/Applications/iMazing Converter.app" 2>/dev/null || true
elif [ -f "/Applications/iMazing Converter.app" ]; then
    rm -f "/Applications/iMazing Converter.app" 2>/dev/null || true
fi

