echo "Removing $HOME/Library/Caches/Insta360/Insta360 Link Controller..."
if [ -d "$HOME/Library/Caches/Insta360/Insta360 Link Controller" ]; then
    rm -rf "$HOME/Library/Caches/Insta360/Insta360 Link Controller" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/Insta360/Insta360 Link Controller" ]; then
    rm -f "$HOME/Library/Caches/Insta360/Insta360 Link Controller" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.insta360.linkcontroller.savedState
echo "Removing $HOME/Library/Saved Application State/com.insta360.linkcontroller.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.insta360.linkcontroller.savedState" ]; then
