#!/bin/bash
# Uninstall script for IntelliDock
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling IntelliDock..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping IntelliDock if running..."
pkill -f "IntelliDock" 2>/dev/null || true

# Remove /Applications/IntelliDock.app
echo "Removing /Applications/IntelliDock.app..."
if [ -d "/Applications/IntelliDock.app" ]; then
    rm -rf "/Applications/IntelliDock.app" 2>/dev/null || true
elif [ -f "/Applications/IntelliDock.app" ]; then
    rm -f "/Applications/IntelliDock.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/app.mightymac.IntelliDock.plist
echo "Removing $HOME/Library/Preferences/app.mightymac.IntelliDock.plist..."
if [ -d "$HOME/Library/Preferences/app.mightymac.IntelliDock.plist" ]; then
    rm -rf "$HOME/Library/Preferences/app.mightymac.IntelliDock.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/app.mightymac.IntelliDock.plist" ]; then
    rm -f "$HOME/Library/Preferences/app.mightymac.IntelliDock.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
