#!/bin/bash
# Uninstall script for InVesalius
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling InVesalius..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping InVesalius if running..."
pkill -f "InVesalius" 2>/dev/null || true

# Remove /Applications/InVesalius.app
echo "Removing /Applications/InVesalius.app..."
if [ -d "/Applications/InVesalius.app" ]; then
    rm -rf "/Applications/InVesalius.app" 2>/dev/null || true
elif [ -f "/Applications/InVesalius.app" ]; then
    rm -f "/Applications/InVesalius.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
