#!/bin/bash
# Uninstall script for Iris
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Iris..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Iris if running..."
pkill -f "Iris" 2>/dev/null || true

# Unload service co.iristech.Iris
echo "Unloading service co.iristech.Iris..."
launchctl unload -w /Library/LaunchAgents/co.iristech.Iris.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/co.iristech.Iris.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/co.iristech.Iris.plist 2>/dev/null || true

# Kill application with bundle ID co.iristech.Iris if running
echo "Stopping application with bundle ID co.iristech.Iris if running..."
killall -9 "co.iristech.Iris" 2>/dev/null || true

# Remove /Applications/Iris.app
echo "Removing /Applications/Iris.app..."
if [ -d "/Applications/Iris.app" ]; then
    rm -rf "/Applications/Iris.app" 2>/dev/null || true
elif [ -f "/Applications/Iris.app" ]; then
    rm -f "/Applications/Iris.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.iristech.Iris.plist
echo "Removing $HOME/Library/Preferences/com.iristech.Iris.plist..."
if [ -d "$HOME/Library/Preferences/com.iristech.Iris.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.iristech.Iris.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.iristech.Iris.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.iristech.Iris.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/co.iristech.Iris.savedState
echo "Removing $HOME/Library/Saved Application State/co.iristech.Iris.savedState..."
if [ -d "$HOME/Library/Saved Application State/co.iristech.Iris.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/co.iristech.Iris.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/co.iristech.Iris.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/co.iristech.Iris.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
