#!/bin/bash
# Uninstall script for JASP
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling JASP..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping JASP if running..."
pkill -f "JASP" 2>/dev/null || true

# Remove /Applications/JASP.app
echo "Removing /Applications/JASP.app..."
if [ -d "/Applications/JASP.app" ]; then
    rm -rf "/Applications/JASP.app" 2>/dev/null || true
elif [ -f "/Applications/JASP.app" ]; then
    rm -f "/Applications/JASP.app" 2>/dev/null || true
fi

# Remove $HOME/.JASP
echo "Removing $HOME/.JASP..."
if [ -d "$HOME/.JASP" ]; then
    rm -rf "$HOME/.JASP" 2>/dev/null || true
elif [ -f "$HOME/.JASP" ]; then
    rm -f "$HOME/.JASP" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/JASP
echo "Removing $HOME/Library/Application Support/JASP..."
if [ -d "$HOME/Library/Application Support/JASP" ]; then
    rm -rf "$HOME/Library/Application Support/JASP" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/JASP" ]; then
    rm -f "$HOME/Library/Application Support/JASP" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/JASP
echo "Removing $HOME/Library/Caches/JASP..."
if [ -d "$HOME/Library/Caches/JASP" ]; then
    rm -rf "$HOME/Library/Caches/JASP" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/JASP" ]; then
    rm -f "$HOME/Library/Caches/JASP" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/org.jasp-stats.JASP.plist
echo "Removing $HOME/Library/Preferences/org.jasp-stats.JASP.plist..."
if [ -d "$HOME/Library/Preferences/org.jasp-stats.JASP.plist" ]; then
    rm -rf "$HOME/Library/Preferences/org.jasp-stats.JASP.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/org.jasp-stats.JASP.plist" ]; then
    rm -f "$HOME/Library/Preferences/org.jasp-stats.JASP.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/org.jasp-stats.jasp.savedState
echo "Removing $HOME/Library/Saved Application State/org.jasp-stats.jasp.savedState..."
if [ -d "$HOME/Library/Saved Application State/org.jasp-stats.jasp.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/org.jasp-stats.jasp.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/org.jasp-stats.jasp.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/org.jasp-stats.jasp.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
