#!/bin/bash
# Uninstall script for Jiggler
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Jiggler..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Jiggler if running..."
pkill -f "Jiggler" 2>/dev/null || true

# Remove /Applications/Jiggler.app
echo "Removing /Applications/Jiggler.app..."
if [ -d "/Applications/Jiggler.app" ]; then
    rm -rf "/Applications/Jiggler.app" 2>/dev/null || true
elif [ -f "/Applications/Jiggler.app" ]; then
    rm -f "/Applications/Jiggler.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.stick.app.jiggler.plist
echo "Removing $HOME/Library/Preferences/com.stick.app.jiggler.plist..."
if [ -d "$HOME/Library/Preferences/com.stick.app.jiggler.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.stick.app.jiggler.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.stick.app.jiggler.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.stick.app.jiggler.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
