#!/bin/bash
# Uninstall script for Jitsi Meet
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Jitsi Meet..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Jitsi Meet if running..."
pkill -f "Jitsi Meet" 2>/dev/null || true

# Remove /Applications/Jitsi Meet.app
echo "Removing /Applications/Jitsi Meet.app..."
if [ -d "/Applications/Jitsi Meet.app" ]; then
    rm -rf "/Applications/Jitsi Meet.app" 2>/dev/null || true
elif [ -f "/Applications/Jitsi Meet.app" ]; then
    rm -f "/Applications/Jitsi Meet.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Jitsi Meet
echo "Removing $HOME/Library/Application Support/Jitsi Meet..."
if [ -d "$HOME/Library/Application Support/Jitsi Meet" ]; then
    rm -rf "$HOME/Library/Application Support/Jitsi Meet" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Jitsi Meet" ]; then
    rm -f "$HOME/Library/Application Support/Jitsi Meet" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/Jitsi Meet
echo "Removing $HOME/Library/Logs/Jitsi Meet..."
if [ -d "$HOME/Library/Logs/Jitsi Meet" ]; then
    rm -rf "$HOME/Library/Logs/Jitsi Meet" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/Jitsi Meet" ]; then
    rm -f "$HOME/Library/Logs/Jitsi Meet" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
