#!/bin/bash
# Uninstall script for Jump Desktop
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Jump Desktop..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Jump Desktop if running..."
pkill -f "Jump Desktop" 2>/dev/null || true

# Remove /Applications/Jump Desktop.app
echo "Removing /Applications/Jump Desktop.app..."
if [ -d "/Applications/Jump Desktop.app" ]; then
    rm -rf "/Applications/Jump Desktop.app" 2>/dev/null || true
elif [ -f "/Applications/Jump Desktop.app" ]; then
    rm -f "/Applications/Jump Desktop.app" 2>/dev/null || true
fi

# Remove $HOME/Documents/JumpDesktop
