#!/bin/bash
# Uninstall script for Kdenlive
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Kdenlive..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Kdenlive if running..."
pkill -f "Kdenlive" 2>/dev/null || true

# Remove /Applications/kdenlive.app
echo "Removing /Applications/kdenlive.app..."
if [ -d "/Applications/kdenlive.app" ]; then
    rm -rf "/Applications/kdenlive.app" 2>/dev/null || true
elif [ -f "/Applications/kdenlive.app" ]; then
    rm -f "/Applications/kdenlive.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/kdenlive
echo "Removing $HOME/Library/Application Support/kdenlive..."
if [ -d "$HOME/Library/Application Support/kdenlive" ]; then
    rm -rf "$HOME/Library/Application Support/kdenlive" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/kdenlive" ]; then
    rm -f "$HOME/Library/Application Support/kdenlive" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/kdenlive
echo "Removing $HOME/Library/Caches/kdenlive..."
if [ -d "$HOME/Library/Caches/kdenlive" ]; then
    rm -rf "$HOME/Library/Caches/kdenlive" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/kdenlive" ]; then
    rm -f "$HOME/Library/Caches/kdenlive" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/kdenlive-layoutsrc
echo "Removing $HOME/Library/Preferences/kdenlive-layoutsrc..."
if [ -d "$HOME/Library/Preferences/kdenlive-layoutsrc" ]; then
    rm -rf "$HOME/Library/Preferences/kdenlive-layoutsrc" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/kdenlive-layoutsrc" ]; then
    rm -f "$HOME/Library/Preferences/kdenlive-layoutsrc" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/kdenliverc
echo "Removing $HOME/Library/Preferences/kdenliverc..."
if [ -d "$HOME/Library/Preferences/kdenliverc" ]; then
    rm -rf "$HOME/Library/Preferences/kdenliverc" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/kdenliverc" ]; then
    rm -f "$HOME/Library/Preferences/kdenliverc" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/org.kde.Kdenlive.plist
echo "Removing $HOME/Library/Preferences/org.kde.Kdenlive.plist..."
if [ -d "$HOME/Library/Preferences/org.kde.Kdenlive.plist" ]; then
    rm -rf "$HOME/Library/Preferences/org.kde.Kdenlive.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/org.kde.Kdenlive.plist" ]; then
    rm -f "$HOME/Library/Preferences/org.kde.Kdenlive.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
