#!/bin/bash
# Uninstall script for KeyStore Explorer
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling KeyStore Explorer..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping KeyStore Explorer if running..."
pkill -f "KeyStore Explorer" 2>/dev/null || true

# Remove /Applications/KeyStore Explorer.app
echo "Removing /Applications/KeyStore Explorer.app..."
if [ -d "/Applications/KeyStore Explorer.app" ]; then
    rm -rf "/Applications/KeyStore Explorer.app" 2>/dev/null || true
elif [ -f "/Applications/KeyStore Explorer.app" ]; then
    rm -f "/Applications/KeyStore Explorer.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/org.kse.keystore-explorer.savedState
echo "Removing $HOME/Library/Saved Application State/org.kse.keystore-explorer.savedState..."
if [ -d "$HOME/Library/Saved Application State/org.kse.keystore-explorer.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/org.kse.keystore-explorer.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/org.kse.keystore-explorer.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/org.kse.keystore-explorer.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
