#!/bin/bash
# Uninstall script for Kobo
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Kobo..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Kobo if running..."
pkill -f "Kobo" 2>/dev/null || true

# Remove /Applications/Kobo.app
echo "Removing /Applications/Kobo.app..."
if [ -d "/Applications/Kobo.app" ]; then
    rm -rf "/Applications/Kobo.app" 2>/dev/null || true
elif [ -f "/Applications/Kobo.app" ]; then
    rm -f "/Applications/Kobo.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Kobo
echo "Removing $HOME/Library/Application Support/Kobo..."
if [ -d "$HOME/Library/Application Support/Kobo" ]; then
    rm -rf "$HOME/Library/Application Support/Kobo" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Kobo" ]; then
    rm -f "$HOME/Library/Application Support/Kobo" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.kobo.Analytics.plist
echo "Removing $HOME/Library/Preferences/com.kobo.Analytics.plist..."
if [ -d "$HOME/Library/Preferences/com.kobo.Analytics.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.kobo.Analytics.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.kobo.Analytics.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.kobo.Analytics.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.kobobooks.Kobo Desktop Edition.plist
echo "Removing $HOME/Library/Preferences/com.kobobooks.Kobo Desktop Edition.plist..."
if [ -d "$HOME/Library/Preferences/com.kobobooks.Kobo Desktop Edition.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.kobobooks.Kobo Desktop Edition.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.kobobooks.Kobo Desktop Edition.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.kobobooks.Kobo Desktop Edition.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.kobo.desktop.kobo.savedState
echo "Removing $HOME/Library/Saved Application State/com.kobo.desktop.kobo.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.kobo.desktop.kobo.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.kobo.desktop.kobo.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.kobo.desktop.kobo.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.kobo.desktop.kobo.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
