#!/bin/bash
# Uninstall script for LaunchBar
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling LaunchBar..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping LaunchBar if running..."
pkill -f "LaunchBar" 2>/dev/null || true

# Remove /Applications/LaunchBar.app
echo "Removing /Applications/LaunchBar.app..."
if [ -d "/Applications/LaunchBar.app" ]; then
    rm -rf "/Applications/LaunchBar.app" 2>/dev/null || true
elif [ -f "/Applications/LaunchBar.app" ]; then
    rm -f "/Applications/LaunchBar.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/LaunchBar
echo "Removing $HOME/Library/Application Support/LaunchBar..."
if [ -d "$HOME/Library/Application Support/LaunchBar" ]; then
    rm -rf "$HOME/Library/Application Support/LaunchBar" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/LaunchBar" ]; then
    rm -f "$HOME/Library/Application Support/LaunchBar" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/at.obdev.LaunchBar
echo "Removing $HOME/Library/Caches/at.obdev.LaunchBar..."
if [ -d "$HOME/Library/Caches/at.obdev.LaunchBar" ]; then
    rm -rf "$HOME/Library/Caches/at.obdev.LaunchBar" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/at.obdev.LaunchBar" ]; then
    rm -f "$HOME/Library/Caches/at.obdev.LaunchBar" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/at.obdev.LaunchBar.plist
echo "Removing $HOME/Library/Preferences/at.obdev.LaunchBar.plist..."
if [ -d "$HOME/Library/Preferences/at.obdev.LaunchBar.plist" ]; then
    rm -rf "$HOME/Library/Preferences/at.obdev.LaunchBar.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/at.obdev.LaunchBar.plist" ]; then
    rm -f "$HOME/Library/Preferences/at.obdev.LaunchBar.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/at.obdev.LaunchBar.savedState
echo "Removing $HOME/Library/Saved Application State/at.obdev.LaunchBar.savedState..."
if [ -d "$HOME/Library/Saved Application State/at.obdev.LaunchBar.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/at.obdev.LaunchBar.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/at.obdev.LaunchBar.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/at.obdev.LaunchBar.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
