#!/bin/bash
# Uninstall script for LibreCAD
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling LibreCAD..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping LibreCAD if running..."
pkill -f "LibreCAD" 2>/dev/null || true

# Remove /Applications/LibreCAD.app
echo "Removing /Applications/LibreCAD.app..."
if [ -d "/Applications/LibreCAD.app" ]; then
    rm -rf "/Applications/LibreCAD.app" 2>/dev/null || true
elif [ -f "/Applications/LibreCAD.app" ]; then
    rm -f "/Applications/LibreCAD.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/LibreCAD
echo "Removing $HOME/Library/Application Support/LibreCAD..."
if [ -d "$HOME/Library/Application Support/LibreCAD" ]; then
    rm -rf "$HOME/Library/Application Support/LibreCAD" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/LibreCAD" ]; then
    rm -f "$HOME/Library/Application Support/LibreCAD" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.librecad.LibreCAD.plist
echo "Removing $HOME/Library/Preferences/com.librecad.LibreCAD.plist..."
if [ -d "$HOME/Library/Preferences/com.librecad.LibreCAD.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.librecad.LibreCAD.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.librecad.LibreCAD.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.librecad.LibreCAD.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.yourcompany.LibreCAD.savedstate
echo "Removing $HOME/Library/Saved Application State/com.yourcompany.LibreCAD.savedstate..."
if [ -d "$HOME/Library/Saved Application State/com.yourcompany.LibreCAD.savedstate" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.yourcompany.LibreCAD.savedstate" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.yourcompany.LibreCAD.savedstate" ]; then
    rm -f "$HOME/Library/Saved Application State/com.yourcompany.LibreCAD.savedstate" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
