#!/bin/bash
# Uninstall script for LightBurn
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling LightBurn..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping LightBurn if running..."
pkill -f "LightBurn" 2>/dev/null || true

# Remove /Applications/LightBurn.app
echo "Removing /Applications/LightBurn.app..."
if [ -d "/Applications/LightBurn.app" ]; then
    rm -rf "/Applications/LightBurn.app" 2>/dev/null || true
elif [ -f "/Applications/LightBurn.app" ]; then
    rm -f "/Applications/LightBurn.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.LightBurnSoftware.LightBurn.plist
echo "Removing $HOME/Library/Preferences/com.LightBurnSoftware.LightBurn.plist..."
if [ -d "$HOME/Library/Preferences/com.LightBurnSoftware.LightBurn.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.LightBurnSoftware.LightBurn.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.LightBurnSoftware.LightBurn.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.LightBurnSoftware.LightBurn.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/LightBurn
echo "Removing $HOME/Library/Preferences/LightBurn..."
if [ -d "$HOME/Library/Preferences/LightBurn" ]; then
    rm -rf "$HOME/Library/Preferences/LightBurn" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/LightBurn" ]; then
    rm -f "$HOME/Library/Preferences/LightBurn" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.LightBurnSoftware.LightBurn.savedState
echo "Removing $HOME/Library/Saved Application State/com.LightBurnSoftware.LightBurn.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.LightBurnSoftware.LightBurn.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.LightBurnSoftware.LightBurn.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.LightBurnSoftware.LightBurn.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.LightBurnSoftware.LightBurn.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
