#!/bin/bash
# Uninstall script for Limitless
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Limitless..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Limitless if running..."
pkill -f "Limitless" 2>/dev/null || true

# Remove /Applications/Limitless.app
echo "Removing /Applications/Limitless.app..."
if [ -d "/Applications/Limitless.app" ]; then
    rm -rf "/Applications/Limitless.app" 2>/dev/null || true
elif [ -f "/Applications/Limitless.app" ]; then
    rm -f "/Applications/Limitless.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/ai.limitless.desktop
echo "Removing $HOME/Library/Caches/ai.limitless.desktop..."
if [ -d "$HOME/Library/Caches/ai.limitless.desktop" ]; then
