fi

# Remove $HOME/Library/Logs/Limitless
echo "Removing $HOME/Library/Logs/Limitless..."
if [ -d "$HOME/Library/Logs/Limitless" ]; then
    rm -rf "$HOME/Library/Logs/Limitless" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/Limitless" ]; then
    rm -f "$HOME/Library/Logs/Limitless" 2>/dev/null || true
fi

