#!/bin/bash
# Uninstall script for Lingon X
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Lingon X..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Lingon X if running..."
pkill -f "Lingon X" 2>/dev/null || true

# Remove /Applications/Lingon X.app
echo "Removing /Applications/Lingon X.app..."
if [ -d "/Applications/Lingon X.app" ]; then
    rm -rf "/Applications/Lingon X.app" 2>/dev/null || true
elif [ -f "/Applications/Lingon X.app" ]; then
    rm -f "/Applications/Lingon X.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/com.peterborgapps.LingonX*
echo "Removing $HOME/Library/Application Scripts/com.peterborgapps.LingonX*..."
if [ -d "$HOME/Library/Application Scripts/com.peterborgapps.LingonX*" ]; then
    rm -rf "$HOME/Library/Application Scripts/com.peterborgapps.LingonX*" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/com.peterborgapps.LingonX*" ]; then
    rm -f "$HOME/Library/Application Scripts/com.peterborgapps.LingonX*" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Lingon X
echo "Removing $HOME/Library/Application Support/Lingon X..."
if [ -d "$HOME/Library/Application Support/Lingon X" ]; then
    rm -rf "$HOME/Library/Application Support/Lingon X" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Lingon X" ]; then
