    rm -rf "$HOME/Library/Containers/com.peterborgapps.LingonX*" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/com.peterborgapps.LingonX*" ]; then
    rm -f "$HOME/Library/Containers/com.peterborgapps.LingonX*" 2>/dev/null || true
fi

