#!/bin/bash
# Uninstall script for LocationSimulator
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling LocationSimulator..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping LocationSimulator if running..."
pkill -f "LocationSimulator" 2>/dev/null || true

# Remove /Applications/LocationSimulator.app
echo "Removing /Applications/LocationSimulator.app..."
if [ -d "/Applications/LocationSimulator.app" ]; then
    rm -rf "/Applications/LocationSimulator.app" 2>/dev/null || true
elif [ -f "/Applications/LocationSimulator.app" ]; then
    rm -f "/Applications/LocationSimulator.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/LocationSimulator
echo "Removing $HOME/Library/Application Support/LocationSimulator..."
if [ -d "$HOME/Library/Application Support/LocationSimulator" ]; then
    rm -rf "$HOME/Library/Application Support/LocationSimulator" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/LocationSimulator" ]; then
    rm -f "$HOME/Library/Application Support/LocationSimulator" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.schlaubi.LocationSimulator.plist
echo "Removing $HOME/Library/Preferences/com.schlaubi.LocationSimulator.plist..."
if [ -d "$HOME/Library/Preferences/com.schlaubi.LocationSimulator.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.schlaubi.LocationSimulator.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.schlaubi.LocationSimulator.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.schlaubi.LocationSimulator.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
