#!/bin/bash
# Uninstall script for Loom
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Loom..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Loom if running..."
pkill -f "Loom" 2>/dev/null || true

# Remove /Applications/Loom.app
echo "Removing /Applications/Loom.app..."
if [ -d "/Applications/Loom.app" ]; then
    rm -rf "/Applications/Loom.app" 2>/dev/null || true
elif [ -f "/Applications/Loom.app" ]; then
    rm -f "/Applications/Loom.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Loom
echo "Removing $HOME/Library/Application Support/Loom..."
if [ -d "$HOME/Library/Application Support/Loom" ]; then
    rm -rf "$HOME/Library/Application Support/Loom" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Loom" ]; then
    rm -f "$HOME/Library/Application Support/Loom" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/Loom
echo "Removing $HOME/Library/Logs/Loom..."
if [ -d "$HOME/Library/Logs/Loom" ]; then
    rm -rf "$HOME/Library/Logs/Loom" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/Loom" ]; then
    rm -f "$HOME/Library/Logs/Loom" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.loom.desktop.plist
echo "Removing $HOME/Library/Preferences/com.loom.desktop.plist..."
if [ -d "$HOME/Library/Preferences/com.loom.desktop.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.loom.desktop.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.loom.desktop.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.loom.desktop.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.loom.desktop.savedState
echo "Removing $HOME/Library/Saved Application State/com.loom.desktop.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.loom.desktop.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.loom.desktop.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.loom.desktop.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.loom.desktop.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
