#!/bin/bash
# Uninstall script for LosslessCut
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling LosslessCut..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping LosslessCut if running..."
pkill -f "LosslessCut" 2>/dev/null || true

# Remove /Applications/LosslessCut.app
echo "Removing /Applications/LosslessCut.app..."
if [ -d "/Applications/LosslessCut.app" ]; then
    rm -rf "/Applications/LosslessCut.app" 2>/dev/null || true
elif [ -f "/Applications/LosslessCut.app" ]; then
    rm -f "/Applications/LosslessCut.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/LosslessCut
echo "Removing $HOME/Library/Application Support/LosslessCut..."
if [ -d "$HOME/Library/Application Support/LosslessCut" ]; then
    rm -rf "$HOME/Library/Application Support/LosslessCut" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/LosslessCut" ]; then
    rm -f "$HOME/Library/Application Support/LosslessCut" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/LosslessCut
echo "Removing $HOME/Library/Logs/LosslessCut..."
if [ -d "$HOME/Library/Logs/LosslessCut" ]; then
    rm -rf "$HOME/Library/Logs/LosslessCut" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/LosslessCut" ]; then
    rm -f "$HOME/Library/Logs/LosslessCut" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
