#!/bin/bash
# Uninstall script for LTspice
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling LTspice..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping LTspice if running..."
pkill -f "LTspice" 2>/dev/null || true

# Remove $HOME/Documents/LTspice/examples
echo "Removing $HOME/Documents/LTspice/examples..."
if [ -d "$HOME/Documents/LTspice/examples" ]; then
    rm -rf "$HOME/Documents/LTspice/examples" 2>/dev/null || true
elif [ -f "$HOME/Documents/LTspice/examples" ]; then
    rm -f "$HOME/Documents/LTspice/examples" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.analog.ltspice.app.sfl*
