if [ -d "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.analog.ltspice.app.sfl*" ]; then
    rm -rf "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.analog.ltspice.app.sfl*" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.analog.ltspice.app.sfl*" ]; then
    rm -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.analog.ltspice.app.sfl*" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/LTspice
echo "Removing $HOME/Library/Application Support/LTspice..."
if [ -d "$HOME/Library/Application Support/LTspice" ]; then
    rm -rf "$HOME/Library/Application Support/LTspice" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/LTspice" ]; then
    rm -f "$HOME/Library/Application Support/LTspice" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.analog.LTspice.App.plist
echo "Removing $HOME/Library/Preferences/com.analog.LTspice.App.plist..."
if [ -d "$HOME/Library/Preferences/com.analog.LTspice.App.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.analog.LTspice.App.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.analog.LTspice.App.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.analog.LTspice.App.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.analog.LTspice.App.savedState
echo "Removing $HOME/Library/Saved Application State/com.analog.LTspice.App.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.analog.LTspice.App.savedState" ]; then
