elif [ -f "$HOME/Library/Saved Application State/com.analog.LTspice.App.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.analog.LTspice.App.savedState" 2>/dev/null || true
fi

# Remove $HOME/Documents/LTspice
echo "Removing $HOME/Documents/LTspice..."
if [ -d "$HOME/Documents/LTspice" ]; then
    rm -rf "$HOME/Documents/LTspice" 2>/dev/null || true
elif [ -f "$HOME/Documents/LTspice" ]; then
    rm -f "$HOME/Documents/LTspice" 2>/dev/null || true
fi

