#!/bin/bash
# Uninstall script for Lunacy
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Lunacy..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Lunacy if running..."
pkill -f "Lunacy" 2>/dev/null || true

# Remove /Applications/Lunacy.app
echo "Removing /Applications/Lunacy.app..."
if [ -d "/Applications/Lunacy.app" ]; then
    rm -rf "/Applications/Lunacy.app" 2>/dev/null || true
elif [ -f "/Applications/Lunacy.app" ]; then
    rm -f "/Applications/Lunacy.app" 2>/dev/null || true
fi

# Remove $HOME/.local/share/Icons8/Lunacy
echo "Removing $HOME/.local/share/Icons8/Lunacy..."
if [ -d "$HOME/.local/share/Icons8/Lunacy" ]; then
    rm -rf "$HOME/.local/share/Icons8/Lunacy" 2>/dev/null || true
elif [ -f "$HOME/.local/share/Icons8/Lunacy" ]; then
    rm -f "$HOME/.local/share/Icons8/Lunacy" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
