#!/bin/bash
# Uninstall script for Mailspring
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Mailspring..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Mailspring if running..."
pkill -f "Mailspring" 2>/dev/null || true

# Remove /Applications/Mailspring.app
echo "Removing /Applications/Mailspring.app..."
if [ -d "/Applications/Mailspring.app" ]; then
    rm -rf "/Applications/Mailspring.app" 2>/dev/null || true
elif [ -f "/Applications/Mailspring.app" ]; then
    rm -f "/Applications/Mailspring.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Mailspring
echo "Removing $HOME/Library/Application Support/Mailspring..."
if [ -d "$HOME/Library/Application Support/Mailspring" ]; then
    rm -rf "$HOME/Library/Application Support/Mailspring" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Mailspring" ]; then
    rm -f "$HOME/Library/Application Support/Mailspring" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.mailspring.*
echo "Removing $HOME/Library/Caches/com.mailspring.*..."
if [ -d "$HOME/Library/Caches/com.mailspring.*" ]; then
    rm -rf "$HOME/Library/Caches/com.mailspring.*" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.mailspring.*" ]; then
    rm -f "$HOME/Library/Caches/com.mailspring.*" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/Mailspring
echo "Removing $HOME/Library/Logs/Mailspring..."
