    rm -rf "$HOME/Library/Preferences/com.mailspring.*" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.mailspring.*" ]; then
    rm -f "$HOME/Library/Preferences/com.mailspring.*" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.mailspring.*
echo "Removing $HOME/Library/Saved Application State/com.mailspring.*..."
if [ -d "$HOME/Library/Saved Application State/com.mailspring.*" ]; then
