#!/bin/bash
# Uninstall script for MarginNote
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling MarginNote..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping MarginNote if running..."
pkill -f "MarginNote" 2>/dev/null || true

# Remove /Applications/MarginNote 4.app
echo "Removing /Applications/MarginNote 4.app..."
if [ -d "/Applications/MarginNote 4.app" ]; then
    rm -rf "/Applications/MarginNote 4.app" 2>/dev/null || true
elif [ -f "/Applications/MarginNote 4.app" ]; then
    rm -f "/Applications/MarginNote 4.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/QReader.MarginStudyMac
echo "Removing $HOME/Library/Application Support/QReader.MarginStudyMac..."
if [ -d "$HOME/Library/Application Support/QReader.MarginStudyMac" ]; then
    rm -rf "$HOME/Library/Application Support/QReader.MarginStudyMac" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/QReader.MarginStudyMac" ]; then
    rm -f "$HOME/Library/Application Support/QReader.MarginStudyMac" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/QReader.MarginStudyMac
echo "Removing $HOME/Library/Containers/QReader.MarginStudyMac..."
if [ -d "$HOME/Library/Containers/QReader.MarginStudyMac" ]; then
    rm -rf "$HOME/Library/Containers/QReader.MarginStudyMac" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/QReader.MarginStudyMac" ]; then
    rm -f "$HOME/Library/Containers/QReader.MarginStudyMac" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
