#!/bin/bash
# Uninstall script for MEGA
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling MEGA..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping MEGA if running..."
pkill -f "MEGA" 2>/dev/null || true

# Kill application with bundle ID com.company.MEGA12 if running
echo "Stopping application with bundle ID com.company.MEGA12 if running..."
killall -9 "com.company.MEGA12" 2>/dev/null || true

# Remove $HOME/Library/Preferences/com.company.MEGA*
echo "Removing $HOME/Library/Preferences/com.company.MEGA*..."
if [ -d "$HOME/Library/Preferences/com.company.MEGA*" ]; then
    rm -rf "$HOME/Library/Preferences/com.company.MEGA*" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.company.MEGA*" ]; then
    rm -f "$HOME/Library/Preferences/com.company.MEGA*" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.company.MEGA*
echo "Removing $HOME/Library/Saved Application State/com.company.MEGA*..."
if [ -d "$HOME/Library/Saved Application State/com.company.MEGA*" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.company.MEGA*" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.company.MEGA*" ]; then
    rm -f "$HOME/Library/Saved Application State/com.company.MEGA*" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
