#!/bin/bash
# Uninstall script for MenubarX
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling MenubarX..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping MenubarX if running..."
pkill -f "MenubarX" 2>/dev/null || true

# Remove /Applications/MenubarX.app
echo "Removing /Applications/MenubarX.app..."
if [ -d "/Applications/MenubarX.app" ]; then
    rm -rf "/Applications/MenubarX.app" 2>/dev/null || true
elif [ -f "/Applications/MenubarX.app" ]; then
    rm -f "/Applications/MenubarX.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/com.app.menubarx
