#!/bin/bash
# Uninstall script for Merlin Project
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Merlin Project..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Merlin Project if running..."
pkill -f "Merlin Project" 2>/dev/null || true

# Remove /Applications/Merlin Project.app
echo "Removing /Applications/Merlin Project.app..."
if [ -d "/Applications/Merlin Project.app" ]; then
    rm -rf "/Applications/Merlin Project.app" 2>/dev/null || true
elif [ -f "/Applications/Merlin Project.app" ]; then
    rm -f "/Applications/Merlin Project.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/net.projectwizards.merlinproject
