# Remove $HOME/Library/Containers/net.projectwizards.merlinproject
echo "Removing $HOME/Library/Containers/net.projectwizards.merlinproject..."
if [ -d "$HOME/Library/Containers/net.projectwizards.merlinproject" ]; then
