#!/bin/bash
# Uninstall script for Min
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Min..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Min if running..."
pkill -f "Min" 2>/dev/null || true

# Remove /Applications/Min.app
echo "Removing /Applications/Min.app..."
if [ -d "/Applications/Min.app" ]; then
    rm -rf "/Applications/Min.app" 2>/dev/null || true
elif [ -f "/Applications/Min.app" ]; then
    rm -f "/Applications/Min.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Min
echo "Removing $HOME/Library/Application Support/Min..."
if [ -d "$HOME/Library/Application Support/Min" ]; then
    rm -rf "$HOME/Library/Application Support/Min" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Min" ]; then
    rm -f "$HOME/Library/Application Support/Min" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/Min
echo "Removing $HOME/Library/Caches/Min..."
if [ -d "$HOME/Library/Caches/Min" ]; then
    rm -rf "$HOME/Library/Caches/Min" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/Min" ]; then
    rm -f "$HOME/Library/Caches/Min" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.electron.min.savedState
echo "Removing $HOME/Library/Saved Application State/com.electron.min.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.electron.min.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.electron.min.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.electron.min.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.electron.min.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
