#!/bin/bash
# Uninstall script for Minecraft
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Minecraft..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Minecraft if running..."
pkill -f "Minecraft" 2>/dev/null || true

# Remove /Applications/Minecraft.app
echo "Removing /Applications/Minecraft.app..."
if [ -d "/Applications/Minecraft.app" ]; then
    rm -rf "/Applications/Minecraft.app" 2>/dev/null || true
elif [ -f "/Applications/Minecraft.app" ]; then
    rm -f "/Applications/Minecraft.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Minecraft Launcher
echo "Removing $HOME/Library/Application Support/Minecraft Launcher..."
if [ -d "$HOME/Library/Application Support/Minecraft Launcher" ]; then
    rm -rf "$HOME/Library/Application Support/Minecraft Launcher" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Minecraft Launcher" ]; then
    rm -f "$HOME/Library/Application Support/Minecraft Launcher" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.mojang.minecraftlauncher
echo "Removing $HOME/Library/Caches/com.mojang.minecraftlauncher..."
if [ -d "$HOME/Library/Caches/com.mojang.minecraftlauncher" ]; then
    rm -rf "$HOME/Library/Caches/com.mojang.minecraftlauncher" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.mojang.minecraftlauncher" ]; then
    rm -f "$HOME/Library/Caches/com.mojang.minecraftlauncher" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.mojang.minecraftlauncherupdater
echo "Removing $HOME/Library/Caches/com.mojang.minecraftlauncherupdater..."
if [ -d "$HOME/Library/Caches/com.mojang.minecraftlauncherupdater" ]; then
    rm -rf "$HOME/Library/Caches/com.mojang.minecraftlauncherupdater" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.mojang.minecraftlauncherupdater" ]; then
    rm -f "$HOME/Library/Caches/com.mojang.minecraftlauncherupdater" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
